record.field:
active_ids = context.get('active_ids') moves = env['stock.move'].browse(active_ids) if moves.filtered(lambda x: x.state not in ('waiting', 'confirmed', 'assigned')): raise Warning('aaa')
if moves.filtered('requisition_id'): raise Warning('aaa')
lines = [] for move in moves: lines.append((0, 0, {'product_id': move.product_id.id, 'product_uom_id': move.product_uom.id, 'product_qty': move.product_qty, 'name': move.name})) requisition = env['purchase.requisition'].create({'line_ids': lines}) moves.write({'requisition_id': requisition.id})
